package vgbox.vgboxDB.VO;

public class Seller {
	private String id;
	private String password;
	private String name;
	private String address;
	private String contact;
	private String business_num;
	private String auth;
	private String grade_name;
	public Seller() {
		super();
		// TODO Auto-generated constructor stub
	}

	public Seller(String id, String password, String name, String address, String contact, String business_num,
			String auth, String grade_name) {
		super();
		this.id = id;
		this.password = password;
		this.name = name;
		this.address = address;
		this.contact = contact;
		this.business_num = business_num;
		this.auth = auth;
		this.grade_name = grade_name;
	}

	public String getBusiness_num() {
		return business_num;
	}

	public void setBusiness_num(String business_num) {
		this.business_num = business_num;
	}

	public String getGrade_name() {
		return grade_name;
	}

	public void setGrade_name(String grade_name) {
		this.grade_name = grade_name;
	}

	public String getId() {
		return id;
	}

	public void setId(String id) {
		this.id = id;
	}

	public String getAuth() {
		return auth;
	}

	public void setAuth(String auth) {
		this.auth = auth;
	}



	public String getPassword() {
		return password;
	}
	public void setPassword(String password) {
		this.password = password;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public String getAddress() {
		return address;
	}
	public void setAddress(String address) {
		this.address = address;
	}
	public String getContact() {
		return contact;
	}
	public void setContact(String contact) {
		this.contact = contact;
	}

}
